// 如果使用组合订阅模板中，自动选择中继节点的链式代理脚本，可以启用这个脚本隐藏中继选择策略组
function main(config) {
  config["proxy-groups"].forEach((group) => {
    if (group.name === "中继选择") {
      group.hidden = true;
    }
  });
  return config;
}

function main(config) {
  config["proxy-groups"].forEach((group) => {
    if (group.name === "香港节点" || 
        group.name === "日本节点" ||
        group.name === "韩国节点" ||
        group.name === "狮城节点" ||
        group.name === "美国节点" ||
        group.name === "英国节点" ||
        group.name === "法国节点" ||
        group.name === "德国节点" ||
        group.name === "台湾节点" ) {
      group.hidden = false;
    }
  });
  return config;
}
